mapper.update:
UPDATE company_cover_letter
SET ccl_letter_question = #{cclLetterQuestion},
    ccl_min_length      = #{cclMinLength},
    ccl_max_length      = #{cclMaxLength}
WHERE ccl_idx = #{cclIdx}

mapper.delete:
DELETE
FROM company_cover_letter
WHERE ccl_idx = #{cclIdx}
  and ccl_com_idx = #{cclComIdx}
