mapper.select:
SELECT *
FROM member

    username IN (
    SELECT m.username
    FROM member m
    LEFT JOIN member_role r ON m.username = r.username
    WHERE m.com_idx = #{comIdx}
    GROUP BY m.username
    HAVING COUNT(m.username) = 1
    )


        AND name LIKE CONCAT('%', #{keyword}, '%')


        AND pg_idx = #{currPgIdx}


        AND cover_letter_status = #{coverLetterFilter}


        AND resume_status = #{resumeFilter}


        AND interview_status = #{interviewFilter}


        AND pg_reg_status = #{regStatusFilter}


ORDER BY join_date DESC
LIMIT #{pageable.pageSize}
OFFSET #{pageable.offset}

SELECT COUNT(*)
FROM member

    username IN (
    SELECT m.username
    FROM member m
    LEFT JOIN member_role r ON m.username = r.username
    WHERE m.com_idx = #{comIdx}
    GROUP BY m.username
    HAVING COUNT(m.username) = 1
    )


        AND name LIKE CONCAT('%', #{keyword}, '%')


        AND pg_idx = #{currPgIdx}


        AND cover_letter_status = #{coverLetterFilter}


        AND resume_status = #{resumeFilter}


        AND interview_status = #{interviewFilter}


        AND pg_reg_status = #{regStatusFilter}

SELECT count(*)
FROM member
WHERE username = #{username}

mapper.update:
UPDATE member
SET com_idx = #{comIdx}
WHERE idx = #{memIdx}

UPDATE member
SET pg_idx = #{pgIdx}
WHERE idx = #{memIdx}

UPDATE member
SET pg_reg_status = #{pgRegStatus}
WHERE idx = #{memIdx}

UPDATE member
    SET pg_reg_date = #{date}
WHERE idx = #{memIdx}

mapper.select:
SELECT COUNT(*)
FROM member m
         JOIN program p ON m.pg_idx = p.pg_idx
WHERE m.pg_reg_status = #{pgRegStatus}
  and m.pg_idx = #{pgIdx}

mapper.update:
UPDATE member
SET registered_interview_date = #{registeredInterviewDate}
WHERE idx = #{memIdx}

UPDATE member
SET interview_status = #{interviewStatus}
WHERE idx=#{memIdx}

UPDATE member
SET profile_img = #{profileImg}
WHERE idx = #{idx}
